function isPositiveNumber(num) {
	var positive = false;
	var number   = false;

	if( Math.max(0, num) == num ) { positive = true; }
	if( Math.ceil(num) == num )   { number   = true; }
	if( positive && number ) { return true; } else { return false; }
}

